insert into games (nome, imagem, preco, sessao_id,descricao) values ('God of war','https://images-americanas.b2w.io/produtos/01/00/img/1227812/3/1227812329_2SZ.jpg', 99.90,2, 'É um novo começo para Kratos. Vivendo como um homem longe da sombra dos deuses, ele se aventura pelas selvagens florestas nórdicas com seu filho Atreus, lutando para cumprir uma missão profundamente pessoal.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('The last of us 2', 'https://images-americanas.b2w.io/produtos/01/00/img/1459449/0/1459449098_1SZ.jpg', 135.90,2, 'Cinco anos depois da jornada perigosa pelos Estados Unidos pós-pandêmicos, Ellie e Joel se estabelecem em Jackson, Wyoming. A vida em uma próspera comunidade de sobreviventes lhes trouxe paz e estabilidade, apesar da ameaça constante dos infectados e de outros sobreviventes mais desesperados. Quando um evento violento interrompe essa paz, Ellie embarca em uma jornada implacável para fazer justiça e encontrar uma solução. Enquanto vai atrás de cada um dos responsáveis, ela se confronta com as repercussões físicas e emocionais devastadoras de suas ações.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('Watch Dogs Legion', 'https://images-americanas.b2w.io/produtos/01/00/img/2079217/8/2079217842_1SZ.jpg', 39.90,1, 'Está na hora de retomar Londres, e você pode recrutar qualquer pessoa à sua vista para a sua resistência. Oportunistas corruptos estão no controle e cabe a você criar uma resistência para devolver a cidade para o povo. Com uma população inteira de potenciais recrutas e a tecnologia da cidade na ponta dos seus dedos, você precisará realizar hacks, infiltrar e lutar para liberar Londres. Bem-vindo à Resistência.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('Forza Horizon 5', 'https://images-americanas.b2w.io/produtos/4371985814/imagens/forza-horizon-5-xbox-one-xbox-series-x/4371985814_1_xlarge.jpg', 159.90,1, 'Sua Aventura Definitiva de Horizon o aguarda! Explore as paisagens do mundo aberto vibrante e em constante evolução do México, com uma ação de direção divertida e ilimitada em centenas dos melhores carros do mundo.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('Dying Light','../Assets/img/webp/dying_light.webp',39.90, 2, 'O vírus venceu e a civilização sucumbiu à Era das Trevas. A cidade, uma das últimas grandes colônias humanas, está à beira do colapso. Use sua agilidade e suas habilidades de combate para sobreviver e reconstruir o mundo. Suas escolhas importam.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('CyberPunk', 'https://images-americanas.b2w.io/produtos/01/00/img/1466225/4/1466225463_1SZ.jpg', 39.90, 2, 'Cyberpunk 2077 é uma história de ação e aventura de mundo aberto ambientada em Night City, uma megalópole obcecada por poder, glamour e biomodificações. Você joga como V, um mercenário fora da lei atrás de um implante único que carrega a chave da imortalidade. Você pode personalizar aparatos cibernéticos, conjunto de habilidades e estilo de jogo do personagem e explorar uma vasta cidade onde as decisões tomadas definem a história e o mundo ao seu redor.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('BF 2042 - PS5', 'https://images-americanas.b2w.io/produtos/4378763991/imagens/battlefield-2042-bf-2042-para-ps4/4378763991_1_xlarge.jpg', 179.91, 2, 'Num futuro próximo mergulhado no caos, adapta-se e supera em campos de batalha dinâmicos com a ajuda do teu esquadrão e de um arsenal de ponta. Com suporte para até 128 jogadoresnos consoles de nova geração (geração antiga 64 jogadores), Battlefield 2042 traz consigo uma escala sem precedentes em vastos campos de batalha globais.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('Days Gone', 'https://images-americanas.b2w.io/produtos/01/00/img/134195/7/134195755_2SZ.jpg', 59.80, 2, 'Days Gone é um jogo de mundo aberto com zumbis em um cenário pós-apocalíptico jogado em uma perspectiva de terceira-pessoa. Os jogadores controlam Deacon St. John, um viajante e caçador de recompensas que prefere viver uma vida perigosa na estrada, ao invés da vida nos acampamentos na floresta.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('Anthem', '../Assets/img/webp/anthem.webp', 59.80, 2, 'Mostre sua força. Em um mundo inacabado pelos deuses, uma facção sombria ameaça toda a humanidade. Só você pode impedir que os agentes do Dominion consigam o poder ancestral que tanto cobiçam. Forme uma equipe neste RPG de ação cooperativa criado pela BioWare™ e EA');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('FarCry Primal', '../Assets/img/webp/far_cry_primal.webp', 59.80, 2, 'Bem-vindo à Idade da Pedra, uma época de perigos por toda parte e aventuras sem limites em que mamutes-lanosos gigantes e tigres-dentes-de-sabre governam a Terra e os humanos estão no fundo da cadeia alimentar. Como o último sobrevivente da sua tribo, você vai aprender a criar um arsenal letal, a se defender de predadores ferozes e a ser mais esperto que os inimigos para conquistar Oros e se tornar um Superpredador.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('FarCry 6', '../Assets/img/webp/far_cry_6.webp', 59.80, 2, 'Esta é Yara, um paraíso tropical congelado no tempo. Far Cry 6 leva os jogadores a um mundo cheio de adrenalina durante uma revolução de guerrilha moderna. Junte-se à revolução e enfrente o regime opressor do ditador Antón Castillo e do filho Diego, interpretados pelas estrelas de Hollywood Giancarlo Esposito (O Mandaloriano, Breaking Bad) e Anthony Gonzalez (Viva - A Vida é uma Festa).');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('FIFA 22', '../Assets/img/webp/fifa22.webp', 59.80, 2, 'Jogue o Maior Jogo do Mundo com +17.000 jogadores, mais de 700 times em +90 estádios e mais de 30 ligas de todo o mundo.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('Ghost of Tsushima', '../Assets/img/webp/ghost_of_tsushima.webp', 59.80, 2, 'Descubra as maravilhas ocultas de Tsushima nesse jogo de ação e aventura em mundo aberto da Sucker Punch Productions e do PlayStation Studios, disponível para PS5 e PS4. Forje um novo caminho e trave uma guerra atípica pela liberdade de Tsushima. Desafie oponentes com sua katana, domine o arco para eliminar ameaças distantes, desenvolva táticas furtivas para emboscar inimigos e explore uma nova história na Ilha Iki.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('Grand Theft Auto 5', '../Assets/img/webp/gta5PS4.webp', 59.80, 2, 'Quando um malandro de rua, um ladrão de bancos aposentado e um psicopata aterrorizante se envolvem com alguns dos criminosos mais assustadores e loucos do submundo, o governo dos EUA e a indústria do entretenimento, eles devem realizar golpes ousados para sobreviver nessa cidade implacável onde não podem confiar em ninguém, nem mesmo um no outro.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('Horizon Zero Down', '../Assets/img/webp/horizon.webp', 59.80, 2, 'Em uma era na qual máquinas vagam livremente e a humanidade deixou de ser a espécie dominante, uma jovem caçadora chamada Aloy inicia uma jornada na qual desvendará o seu destino. Em um mundo pós-apocalíptico exuberante, onde a natureza retomou as ruínas de uma civilização esquecida, pequenos grupos de pessoas vivem em tribos primitivas de caçadores e coletores. O domínio delas sobre esse novo ambiente selvagem foi usurpado pelas máquinas, terríveis criaturas mecânicas de origem desconhecida.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('Mortal Kombat X', '../Assets/img/webp/mortal_kombat_Xbox.webp', 59.80, 2, 'Mortal Kombat X combina uma apresentação cinematográfica sem igual com uma jogabilidade nova, formando os Kombates mais brutais da história. Pela primeira vez, Mortal Kombat X possibilita a escolha entre múltiplos estilos para cada personagem, afetando tanto a estratégia como também as técnicas de luta.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('Nioh', '../Assets/img/webp/nioh.webp', 59.80, 2, 'Pegue sua espada e viaje até ao sanguinário período Sengoku no Japão, uma era devastada por estados em guerra e forças sombrias e malévolas, e abra seu caminho pela região no papel de William, um samurai sem senhor. Dispute combates corpo a corpo brutais usando espadas, machados, lanças e até martelos de guerra contra inimigos humanos e demoníacos. Sobreviva aos confrontos brutais e aprenda com seus erros: cada morte trará a ressurreição e cada ressurreição uma determinação ainda maior para ultrapassar seus adversários.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('Ratchet e Clank', '../Assets/img/webp/ratch.webp', 59.80, 2, 'Tanto faz se você é fã de longa data ou acabou de conhecer a série, está na hora de se preparar para uma viagem inesquecível pelo universo. Reviva a história alucinante que deu origem a esta duplinha destemida com uma narrativa nova e mais aprofundada. Você vai ter mais ação, novos chefões para enfrentar, visuais espetaculares e a dublagem sensacional da animação de 2016, Ratchet & Clank™');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('Red Dead Redemption', '../Assets/img/webp/reddead.webp', 59.80, 2, 'O velho oeste está morrendo. Quando agentes federais ameaçam sua família, o antigo fora-da-lei John Marston é forçado a pegar suas armas uma vez mais e caçar a gangue de criminosos que uma vez chamou de amigos. Vivencie uma luta épica pela sobrevivência nas vastas planícies do oeste dos Estados Unidos e do México conforme John Marston faz de tudo para enterrar seu passado sangrento, um homem de cada vez.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('Resident Evil Village', '../Assets/img/webp/reVillagePs4.webp', 59.80, 2, 'Vivencie o horror de sobrevivência como nunca na 8ª grande sequência da franquia Resident Evil - Resident Evil Village. <br/><br/>Ambientado em alguns anos depois dos terríveis eventos do consagrado pela crítica Resident Evil 7 bioharzard, o novo enredo começa com Ethan Winters e sua esposa Mia vivendo pacificamente num novo lugar, livres dos pesadelos do passado. E, logo quando estavam construindo uma nova vida juntos, uma tragédia os acomete novamente.');
insert into games (nome, imagem, preco, sessao_id,descricao) values ('Spider-Man', '../Assets/img/webp/spider.webp', 59.80, 2, 'Days Gone é um jogo de mundo aberto com zumbis em um cenário pós-apocalíptico jogado em uma perspectiva de terceira-pessoa. Os jogadores controlam Deacon St. John, um viajante e caçador de recompensas que prefere viver uma vida perigosa na estrada, ao invés da vida nos acampamentos na floresta.');
